#!/usr/bin/python3
import socket

target_host = "0.0.0.0"
target_port = 9999
# create a socket object
client = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
# connect to the client
client.connect((target_host, target_port))
# send some data
# python3 needs this b'' in the send method
client.send(b'')
# recieve some data
response = client.recv(4096)

print("DATA BACK FROM SERVER->  \r\n{}".format(response))
